import { loggerError } from './util'
import { getEslint } from './eslint'

// eslint 校验
export const execEslint = async () => {
  await getEslint()
}

export const eslintCommand = {
  version: '0.1.0',
  description: 'start eslint and fix code',
  command: 'tEslint',
  action: () => execEslint()
}

export default [
  eslintCommand
]

module.exports = eslintCommand